document.addEventListener("DOMContentLoaded",(()=>{const t=document.querySelectorAll("[data-tab]"),e=document.querySelectorAll("[data-tab-content]");t.forEach((function(t){t.addEventListener("click",(function(){e.forEach((function(t){t.classList.add("hidden")}));document.querySelector("#"+this.dataset.tab).classList.remove("hidden")}))}));for(let e=0;e<t.length;e++)t[e].addEventListener("click",(()=>{for(let e=0;e<t.length;e++)t[e].classList.remove("item--active");t[e].classList.add("item--active")}))}));